mapper.select:
  select parent_column_id,user_type,
  count(distinct user_id) as total_visit_user_count from user_info where create_time <=#{endDate}
group by parent_column_id,user_type

mapper.sql:

    select t_date,user_type,parent_column_id,sum(visit_user_count) as visit_user_count,sum(play_user_count) as
    play_user_count,
    sum(play_count) as play_count,sum(duration) as duration,sum(add_user_count) as
    add_user_count,sum(total_visit_user_count) as total_visit_user_count,
    sum(user_2day_count) as user_2day_count,sum(user_3day_count) as user_3day_count
    from(


    select
    t_date,user_type,parent_column_id,visit_user_count,play_user_count,play_count,duration,add_user_count,total_visit_user_count,
    0 as user_2day_count,0 as user_3day_count from app_count_day where ${ew.expression.normal.sqlSegment}


    UNION ALL


    select t_date,user_type,parent_column_id,0 as visit_user_count,0 as play_user_count,0 as play_count,0 as
    duration,
    0 as add_user_count,0 as total_visit_user_count,user_2day_count,user_3day_count
    from app_retention_user_count_day where ${ew.expression.normal.sqlSegment}


    ) a group by t_date,user_type,parent_column_id

${ew.expression.orderBy.sqlSegment}
